INSERT INTO `wp_posts` VALUES (2057,5,'2014-04-23 16:27:11','2014-04-23 16:27:11','<div id=\"help\" style=\"border:1px solid gray; padding:20px; margin-top:10px; margin-bottom:10px;\">\r\n	<strong>Need help getting started? <a href=\"http://events.braintumor.org/k-participant-support/\" title=\"Participant Support\" target=\"_blank\">View our instructions and video tutorials</a>.</strong>\r\n</div>\r\n\r\n<div id=\"event_url\" style=\"display:none\"></div>\r\n<script>\r\n if ($.cookie(\'event_cookie\')){\r\n  $(\'#event_url\').show();\r\n  $(\'#event_url\').html(\'<a href=\"\'+$.cookie(\'event_cookie\')+\'\"><strong>BACK TO EVENT</strong></a>\');\r\n};\r\n</script>\r\n\r\n<script>\r\nif (window.location.search.indexOf(\'kid=\') == -1) {\r\n    document.write(\'<span style=\\\"color:#00A0DF; font-size:1.5em; font-weight:bold;\\\">Want to access your Participant Center and manage your Personal Page? Enter the email address you used to register and we\\\'ll send your secure link to you via email.</span>\');\r\n}\r\n</script>\r\n\r\n<script src=\'https://widgets.kimbia.com/widgets/commitmentCenter.js?id=3OVK3I6\'></script>\r\n\r\n<div class=\"widget\">\r\n<script src=\'https://widgets.kimbia.com/widgets/commitmentProfile.js?fieldName=teamName\'></script>\r\n<script src=\'https://widgets.kimbia.com/widgets/commitmentProfile.js?fieldName=headline\'></script>\r\n<script src=\'https://widgets.kimbia.com/widgets/commitmentProfile.js?fieldName=image\'></script>\r\n<script src=\'https://widgets.kimbia.com/widgets/commitmentProfile.js?fieldName=statement\'></script>\r\n</div>','manage','','publish','closed','closed','','manage','','','2014-07-04 00:20:06','2014-07-04 00:20:06','',1956,'http://events.braintumor.org/?post_type=events&#038;p=2057',0,'events','',0);
